import{_ as a,o as e,c as t,Q as o}from"./chunks/framework.e90f0c97.js";const u=JSON.parse('{"title":"T6.第三方API&&排序","description":"","frontmatter":{"prev":{"text":"T5.制作blog","link":"./T5.制作blog"},"next":{"text":"T7.Node.js","link":"./T7.Node.js.html"}},"headers":[],"relativePath":"md/前端/T6.第三方API&&排序.md","filePath":"md/前端/T6.第三方API&&排序.md"}'),r={name:"md/前端/T6.第三方API&&排序.md"},l=o('<p><img src="https://pic.imgdb.cn/item/64c4a5611ddac507cc058875.png" alt="img"></p><h1 id="t6-第三方api-排序" tabindex="-1">T6.第三方API&amp;&amp;排序 <a class="header-anchor" href="#t6-第三方api-排序" aria-label="Permalink to &quot;T6.第三方API&amp;&amp;排序&quot;">​</a></h1><blockquote><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki github-dark vp-code-dark"><code><span class="line"><span style="color:#e1e4e8;">难度系数：中等</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#24292e;">难度系数：中等</span></span></code></pre></div><p>在前面的学习中，我们制作的页面都仅仅只使用了本地资源。接下来，我们就要迈入网络的世界了。在本题中，你将会了解互联网通信，学习AJAX，了解前端和后端是如何交互。</p><p>学习完后，你将从前端初级阶段，迈入高级阶段的大门。做好准备，咱们开始吧！</p></blockquote><h2 id="任务" tabindex="-1"><strong>任务</strong> <a class="header-anchor" href="#任务" aria-label="Permalink to &quot;**任务**&quot;">​</a></h2><p>我们将学习前端是如何通过接口（API）与后端进行通信，并对一个完整项目的前后端开发流程进行初步了解！</p><h4 id="题目背景" tabindex="-1">题目背景 <a class="header-anchor" href="#题目背景" aria-label="Permalink to &quot;题目背景&quot;">​</a></h4><blockquote><p>还未接触后端时，青子同学的心里是否有这么一个疑问：前端和后端到底是怎么产生联系的呢？我们在做的事情好像只是用标签和函数把网站一点点搭建起来，要怎么才能让网站“活”起来呢？</p></blockquote><p><strong>要求</strong>制作一个网站，从所提供第三方API中获取七个城市的天气信息渲染到页面中，并提供按钮根据温度进行排序（正序和倒序），相同温度下顺序根据天气决定，顺序为：晴，多云，阴，雨（包括大中小雨、暴雨、雷阵雨、阵雨）。</p><p>如果想在自己的页面调用第三方服务器上的内容，又不愿重新加载页面，就需要我们使用Ajax技术了。一般来说，网络上会提供一些免费的接口，供开发者使用，开发者可通过调用接口来获取相关信息。</p><h2 id="题目所提供api" tabindex="-1"><strong>题目所提供API</strong> <a class="header-anchor" href="#题目所提供api" aria-label="Permalink to &quot;**题目所提供API**&quot;">​</a></h2><p>接口地址：</p><blockquote><p><a href="https://api.seniverse.com/v3/weather/now.json?key=SWJEEWD_M17tT3zGk&amp;location=%E5%9F%8E%E5%B8%82%E5%90%8D" target="_blank" rel="noreferrer">https://api.seniverse.com/v3/weather/now.json?key=SWJEEWD_M17tT3zGk&amp;location=城市名</a></p></blockquote><h2 id="可能遇到的问题" tabindex="-1"><strong>可能遇到的问题</strong> <a class="header-anchor" href="#可能遇到的问题" aria-label="Permalink to &quot;**可能遇到的问题**&quot;">​</a></h2><ul><li>计算机在互联网上如何交互与通信？什么是HTTP与HTTPS？什么是IP协议？</li><li>HTTP请求分为哪些类型？GET和POST的区别？什么是请求行、请求头、请求体？</li><li>HTTP响应是由哪些部分组成的？API往往响应什么类型的数据？了解一下JSON格式</li><li>AJAX是什么？什么是异步？异步有什么作用？</li><li><a href="https://www.runoob.com/ajax/ajax-tutorial.html" target="_blank" rel="noreferrer">菜鸟教程 Ajax</a></li><li><a href="https://developer.mozilla.org/zh-CN/docs/Web/Guide/AJAX" target="_blank" rel="noreferrer">MDN Ajax</a></li><li>异步是什么？</li></ul><h2 id="本题提交方式" tabindex="-1"><strong>本题提交方式</strong> <a class="header-anchor" href="#本题提交方式" aria-label="Permalink to &quot;**本题提交方式**&quot;">​</a></h2><blockquote><p><a href="https://submit.glimmer.org.cn" target="_blank" rel="noreferrer"> 提交点这里 </a></p></blockquote><h2 id="出题者q-a方式" tabindex="-1"><strong>出题者Q&amp;A方式</strong> <a class="header-anchor" href="#出题者q-a方式" aria-label="Permalink to &quot;**出题者Q&amp;A方式**&quot;">​</a></h2><blockquote><p>QQ：3352859153</p></blockquote>',18),s=[l];function i(n,p,c,h,d,m){return e(),t("div",null,s)}const b=a(r,[["render",i]]);export{u as __pageData,b as default};
